
$(document).ready(function(){$("table.graph-table input.ssInputData").click(function(d){if(d.shiftKey){console.log("");var b=$(".btn-group :button.active").val();console.log("function: "+b);var e=$("input[name='shiftClickInput']").val().trim();console.log("Input Box: "+e);if(e==0||e==""){console.log("Please enter a number in the input box");return}var a=$(this).val();console.log("Cell: "+a);switch(b){case"add":var c=parseFloat(Number(a)+Number(e));break;case"subtract":var c=parseFloat(Number(a)-Number(e));break;case"multiply":var c=parseFloat(Number(a)*Number(e));break;case"divide":var c=parseFloat(Number(a)/Number(e));break;case"equal":var c=parseFloat(Number(e));break;default:alert("no operation was selected")}console.log("Value: "+c);$(this).val(c.toFixed(2));$(this).css("border","1px solid lime");$(this).css("background-color","");$(this).css("color","black")}})});